(417, 1, 'InterCharm 2016 ', '<p>Alfa SPA на&nbsp;InterCharm 2016</p>\r\n\r\n<p>&nbsp;</p>\r\n', '<p>Фоторепортаж с выставки&nbsp; InterCharm 2016.</p>\r\n\r\n<p>Alfa SPA представила технологии компании Bio-Therapeutic (USA).</p>\r\n\r\n<p>Bio-Therapeutic &ndash; создатель технологии микротоковой терапии. Компания основана в 1974 году, головной офис находится в Сиэтле, США. Компания является мировым лидером в эстетической косметологии, работает в сфере прогрессивных неинвазивных методик, имеет многочисленные запатентованные и революционные технологии. Аппараты Bio-Therapeutic одобрены FDA (Federal Drug Administration, USA). Компания&nbsp; постоянно совершенствует технологии для косметологов и врачей. Аппараты неоднократно были признаны лучшими технологиями года.</p>\r\n\r\n<p><img alt="" src="/userfiles/images/0215045_n.jpg" style="width: 480px; height: 321px;" /><img alt="" src="/userfiles/images/7778888_n.jpg" style="width: 480px; height: 321px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/7227809_n.jpg" style="width: 480px; height: 321px;" /><img alt="" src="/userfiles/images/9078176_n.jpg" style="width: 480px; height: 321px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/6199320_n.jpg" style="width: 480px; height: 718px;" /><img alt="" src="/userfiles/images/4388963_n.jpg" style="width: 480px; height: 718px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/2145331_n.jpg" style="width: 480px; height: 640px;" /><img alt="" src="/userfiles/images/9226673_n.jpg" style="width: 480px; height: 640px;" /></p>\r\n'),
(417, 2, 'InterCharm. Як це було?', '<p>Фотовідгук з виставки InterCharm 2016.</p>\r\n', '<p>Фоторепортаж с выставки&nbsp; InterCharm 2016.</p>\r\n\r\n<p><img alt="" src="/userfiles/images/0215045_n.jpg" style="width: 480px; height: 321px;" /><img alt="" src="/userfiles/images/7778888_n.jpg" style="width: 480px; height: 321px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/7227809_n.jpg" style="width: 480px; height: 321px;" /><img alt="" src="/userfiles/images/9078176_n.jpg" style="width: 480px; height: 321px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/6199320_n.jpg" style="width: 480px; height: 718px;" /><img alt="" src="/userfiles/images/4388963_n.jpg" style="width: 480px; height: 718px;" /></p>\r\n\r\n<p><img alt="" src="/userfiles/images/2145331_n.jpg" style="width: 480px; height: 640px;" /><img alt="" src="/userfiles/images/9226673_n.jpg" style="width: 480px; height: 640px;" /></p>\r\n'),
